import { Component, OnInit, HostListener } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';

@Component({
  selector: 'app-information',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './information.html',
  styleUrls: ['./information.css'],
})
export class InformationComponent implements OnInit {
  isScrolled = false;

  // Replace 'YOUR_VIDEO_ID' with your actual YouTube video ID
  // Example: if URL is https://www.youtube.com/watch?v=dQw4w9WgXcQ, use 'dQw4w9WgXcQ'
  youtubeVideoId = 'Vd51bCZufHY'; // YouTube video ID

  // Photo URL - replace with your image path or blob URL
  photoUrl =
    'data:image/jpeg;base64,';

  carInfo = () => [
    {
      icon: 'engine',
      title: 'V8 Powerhouse',
      description: 'The heart of the beast',
      value: '4.0L Biturbo',
    },
    {
      icon: 'power',
      title: 'Horsepower',
      description: 'Raw performance unleashed',
      value: '469 HP',
    },
    {
      icon: 'speed',
      title: 'Acceleration',
      description: '0 to 100 km/h',
      value: '4.0 sec',
    },
    {
      icon: 'design',
      title: 'AMG Design',
      description: 'Sporty elegance',
      value: 'Premium',
    },
  ];

  additionalFeatures = () => [
    {
      name: 'AMG Performance Exhaust',
      text: 'Experience the signature AMG sound with the performance exhaust system that delivers an intoxicating V8 roar.',
    },
    {
      name: 'Race Mode',
      text: 'Unleash maximum performance with Race mode, optimized for track driving with enhanced dynamics.',
    },
    {
      name: 'AMG Track Pace',
      text: 'Record and analyze your lap times with precision using the integrated AMG Track Pace system.',
    },
    {
      name: 'Carbon Fiber Elements',
      text: 'Lightweight carbon fiber components reduce weight while enhancing the sporty aesthetic.',
    },
    {
      name: 'AMG Dynamic Select',
      text: 'Choose from multiple driving modes to customize your experience from comfort to race.',
    },
    {
      name: 'Premium Interior',
      text: 'Luxurious materials and AMG Performance seats create the perfect balance of comfort and support.',
    },
  ];

  constructor(private sanitizer: DomSanitizer) {}

  ngOnInit() {
    this.checkScroll();
    this.setupScrollAnimations();
  }

  @HostListener('window:scroll', [])
  onWindowScroll() {
    this.checkScroll();
  }

  private checkScroll() {
    this.isScrolled = window.scrollY > 50;
  }

  safeVideoUrl(): SafeResourceUrl {
    const url = `https://www.youtube.com/embed/${this.youtubeVideoId}?autoplay=0&mute=1&rel=0`;
    return this.sanitizer.bypassSecurityTrustResourceUrl(url);
  }

  private setupScrollAnimations() {
    const observerOptions = {
      threshold: 0.1,
      rootMargin: '0px 0px -50px 0px',
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('animate-in');
        }
      });
    }, observerOptions);

    const elementsToAnimate = document.querySelectorAll(
      '.info-card, .feature-item, .video-card, .photo-card'
    );
    elementsToAnimate.forEach((el) => observer.observe(el));
  }
}
